<template>
  <section class="hero is-fullheight is-medium is-bold">
    <div class="hero-body">
      <div class="container">
        <div class="columns is-centered">
          <div class="column is-three-quarters">
            <article class="card" :class="{'is-waiting': loader}">
              <div class="card-header">
                <div class="card-header-title">
                  管理画面ログイン
                </div>
              </div>
              <div class="card-content">
                <TextInput
                  v-model="email"
                  label="メールアドレス"
                  name="email"
                  type="email"
                  placeholder="メールアドレス"
                  :error="error"
                />
                <TextInput
                  v-model="password"
                  label="パスワード"
                  name="password"
                  type="password"
                  placeholder="パスワード"
                  :error="error"
                />
                <p class="control">
                  <button
                    class="button is-primary is-medium is-fullwidth form__submit"
                    @click.stop.prevent="login()"
                  >
                    ログイン
                  </button>
                </p>
              </div>
            </article>
          </div>
        </div>
      </div>
    </div>
  </section>
</template>

<script lang="ts">
import { Component, Vue } from 'nuxt-property-decorator'
import TextInput from '@/components/form/TextInput.vue'
import { AdminAuthStore } from '@/store'

@Component({
  components: {
    TextInput
  },
  layout: 'admin_login',
  middleware: ['redirectIfAdminAuthenticated']
})
export default class Index extends Vue {
  loader = false
  error = {}
  email = ''
  password = ''
  remember = false

  async login () {
    this.loader = true
    this.error = {}

    await AdminAuthStore.login({ email: this.email, password: this.password })
      .then((data) => {
        AdminAuthStore.saveToken(data.token)
        AdminAuthStore.fetchAdmin(data.admin);
        (this as any).$router.push('/admin')
      })
      .catch((error: any) => {
        this.loader = false
        this.password = ''
        this.error = error.response.data.errors
      })
  }
}
</script>

